mapper.select:

select * from promotion p
inner join tcher t on p.tno = t.tno
where pno > 0 and t.spono = #{typeNo}
order by pno desc
limit 0, 6;


select * from promotion p
inner join tcher t on p.tno = t.tno
where pno <= #{lastNo} 
and pno > #{lastNo}-6
and pno > 0  and t.spono = #{typeNo}

order by pno desc

select  *
from promotion p inner join tcher t  on p.tno=t.tno where t.tno = #{no}
order by p.pno desc

select  *
from promotion p inner join tcher t  on p.tno=t.tno where t.tno = #{no}
order by p.titl desc
