mapper.select:
select mealno, mealpic, mealkcal, mealname, mealtype, date, confirm, day
from memb_manage
where date between #{startDate} and #{endDate}

mapper.update:
update memb_manage set 
  mealpic=#{mealpicture}, 
  mealkcal=#{mealkcal}, 
  mealname=#{mealname} 
where mealno=#{mealno}

